import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
//import axios from "axios";

export const fetchTickets = createAsyncThunk(
    'tickets/fetchTickets',
    // axios.get('https://api.npoint.io/163b5e66df9f2741243e')
    // .then(tickets => dispatch({
    //     type: FETCH_TICKETS,
    //     payload: tickets
    //     })
    // .catch(err)

    async () => {
        const response = await fetch('https://api.npoint.io/163b5e66df9f2741243e');
        const formattedResponse = await response.json();
        return formattedResponse;
    }   

    // async () => {
    //     const response = await fetch('https://jsonplaceholder.typicode.com/posts/?_limit=6');
    //     const formattedResponse = await response.json();
    //     return formattedResponse;
    //}   
)

export const ticketsSlice = createSlice({
    name: 'tickets',
    initialState: {
        tickets: [],
        isLoading: false,
    },
    reducers: {
        chooseCheap(state) {
            state.tickets.sort((prev, next) => prev.price - next.price);
        },
        chooseFast(state) {
            state.tickets.sort((prev, next) => prev.info.duration - next.info.duration
            )
        },
        chooseOptimal(state) {
            state.tickets.sort((prev, next) => prev.price - next.price).sort((prev, next) => prev.info.duration + next.info.duration
            )
        }
    },
    extraReducers: {
        [fetchTickets.pending]: (state) => {
            state.isLoading = true;
        },
        [fetchTickets.fulfilled]: (state, action) => {
            state.tickets = action.payload;
            state.isLoading = false;
        },
        [fetchTickets]: (state) => {
            state.isLoading = false;
        },
        [fetchTickets]: (state) => {
            state.tickets.forEach(item => {
                let unix = item.info.duration;
                let date = new Intl.DateTimeFormat('en-US', { hour: '2-digit', minute: '2-digit', second: '2-digit' }).format(unix)//9:27:16 PM
                console.log(date);
            })
        }
    }
});

export default ticketsSlice.reducer;

export const { chooseCheap, chooseFast, chooseOptimal } = ticketsSlice.actions;
