import { createSlice } from "@reduxjs/toolkit";

export const addSlice = createSlice({
    name: 'add',
    initialState: {
        add: 4,
    },
    reducers: {
        increment: {
            reducer: 
                (state) => state += 5
        }
    }
});

export const { increment } = addSlice.actions;

export default addSlice.reducer;
